			$con = Propel::getConnection(CampaignContactPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(CampaignContactPeer::ID);
			$selectCriteria->add(CampaignContactPeer::ID, $criteria->remove(CampaignContactPeer::ID), $comparison);

			$comparison = $criteria->getComparison(CampaignContactPeer::CAMPAIGN_ID);
			$selectCriteria->add(CampaignContactPeer::CAMPAIGN_ID, $criteria->remove(CampaignContactPeer::CAMPAIGN_ID), $comparison);

			$comparison = $criteria->getComparison(CampaignContactPeer::CONTACT_ID);
			$selectCriteria->add(CampaignContactPeer::CONTACT_ID, $criteria->remove(CampaignContactPeer::CONTACT_ID), $comparison);

		} else { // $values is CampaignContact object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseCampaignContactPeer:doUpdate:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseCampaignContactPeer', $values, $con, $ret);
    }

    return $ret;
