	/**
	 * Method perform a DELETE on the database, given a CampaignContact or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or CampaignContact object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(CampaignContactPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof CampaignContact) { // it's a model object
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else { // it's a primary key, or an array of pks
			$criteria = new Criteria(self::DATABASE_NAME);
			// primary key is composite; we therefore, expect
			// the primary key passed to be an array of pkey values
			if (count($values) == count($values, COUNT_RECURSIVE)) {
				// array is not multi-dimensional
				$values = array($values);
			}
			foreach ($values as $value) {
				$criterion = $criteria->getNewCriterion(CampaignContactPeer::ID, $value[0]);
				$criterion->addAnd($criteria->getNewCriterion(CampaignContactPeer::CAMPAIGN_ID, $value[1]));
				$criterion->addAnd($criteria->getNewCriterion(CampaignContactPeer::CONTACT_ID, $value[2]));
				$criteria->addOr($criterion);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += CampaignContactPeer::doOnDeleteCascade($criteria, $con);
			
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			if ($values instanceof Criteria) {
				CampaignContactPeer::clearInstancePool();
			} elseif ($values instanceof CampaignContact) { // it's a model object
				CampaignContactPeer::removeInstanceFromPool($values);
			} else { // it's a primary key, or an array of pks
				foreach ((array) $values as $singleval) {
					CampaignContactPeer::removeInstanceFromPool($singleval);
				}
			}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);
			CampaignContactPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = CampaignContactPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related CampaignContactBounce objects
			$criteria = new Criteria(CampaignContactBouncePeer::DATABASE_NAME);
			
			$criteria->add(CampaignContactBouncePeer::CAMPAIGN_CONTACT_ID, $obj->getId());
			$affectedRows += CampaignContactBouncePeer::doDelete($criteria, $con);

			// delete related CampaignClick objects
			$criteria = new Criteria(CampaignClickPeer::DATABASE_NAME);
			
			$criteria->add(CampaignClickPeer::CAMPAIGN_CONTACT_ID, $obj->getId());
			$affectedRows += CampaignClickPeer::doDelete($criteria, $con);
		}
		return $affectedRows;
	}

	/**
	 * Validates all modified columns of given CampaignContact object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      CampaignContact $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(CampaignContact $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(CampaignContactPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(CampaignContactPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		return BasePeer::doValidate(CampaignContactPeer::DATABASE_NAME, CampaignContactPeer::TABLE_NAME, $columns);
	}

	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $id
	 * @param      int $campaign_id
	 * @param      int $contact_id
	 * @param      PropelPDO $con
	 * @return     CampaignContact
	 */
	public static function retrieveByPK($id, $campaign_id, $contact_id, PropelPDO $con = null) {
		$key = serialize(array((string) $id, (string) $campaign_id, (string) $contact_id));
 		if (null !== ($obj = CampaignContactPeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(CampaignContactPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(CampaignContactPeer::DATABASE_NAME);
		$criteria->add(CampaignContactPeer::ID, $id);
		$criteria->add(CampaignContactPeer::CAMPAIGN_ID, $campaign_id);
		$criteria->add(CampaignContactPeer::CONTACT_ID, $contact_id);
		$v = CampaignContactPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
